import React, { useEffect, useState } from 'react';
import { getLatestStatus, getStatusHistory, getUserLogs, sendControl } from './services/api';
import { subscribeSensor, publishControl } from './services/mqttService';
import './App.css';

function App() {
    // 初始化 status，防止初始渲染时报错
    const [status, setStatus] = useState({
        temperature: 0,
        pressure: 0,
        depth: 0
    });
    const [history, setHistory] = useState([]);
    const [logs, setLogs] = useState([]);

    useEffect(() => {
        // 获取最新状态
        fetchLatestStatus();
        // 获取历史状态
        fetchHistory();
        // 获取用户日志
        fetchLogs();

        // 订阅 MQTT 传感器数据
        subscribeSensor((message) => {
            // 解析消息
            const regex = /Temperature: ([\d.]+) °C, Pressure: ([\d.]+) kPa, Depth: ([\d.]+) m/;
            const match = message.match(regex);
            if (match) {
                const temperature = parseFloat(match[1]);
                const pressure = parseFloat(match[2]);
                const depth = parseFloat(match[3]);
                setStatus({ temperature, pressure, depth });
                // 可选择更新历史
            }
        });
    }, []);

    const fetchLatestStatus = async () => {
        try {
            const res = await getLatestStatus();
            setStatus(res.data || {
                temperature: 0,
                pressure: 0,
                depth: 0
            });  // 确保返回的数据有效
        } catch (err) {
            console.error(err);
        }
    };

    const fetchHistory = async () => {
        try {
            const res = await getStatusHistory();
            setHistory(res.data || []);  // 确保历史数据存在
        } catch (err) {
            console.error(err);
        }
    };

    const fetchLogs = async () => {
        try {
            const res = await getUserLogs();
            setLogs(res.data || []);  // 确保日志数据存在
        } catch (err) {
            console.error(err);
        }
    };

    const handleControl = async (direction) => {
        try {
            await sendControl(direction);
            // Optionally update logs
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div className="App">
            <h1>水下机器人控制平台</h1>
            <div className="controls">
                <button onClick={() => handleControl('forward')}>前进</button>
                <button onClick={() => handleControl('backward')}>后退</button>
                <button onClick={() => handleControl('left')}>左转</button>
                <button onClick={() => handleControl('right')}>右转</button>
            </div>
            <div className="status">
                <h2>设备状态</h2>
                <p>温度: {status?.temperature ?? 'N/A'} °C</p>
                <p>气压: {status?.pressure ?? 'N/A'} kPa</p>
                <p>深度: {status?.depth ?? 'N/A'} m</p>
            </div>
            <div className="history">
                <h2>历史记录</h2>
                <table>
                    <thead>
                        <tr>
                            <th>时间</th>
                            <th>温度 (°C)</th>
                            <th>气压 (kPa)</th>
                            <th>深度 (m)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {history.map((entry) => (
                            <tr key={entry.id}>
                                <td>{new Date(entry.timestamp).toLocaleString()}</td>
                                <td>{entry.temperature}</td>
                                <td>{entry.pressure}</td>
                                <td>{entry.depth}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="logs">
                <h2>用户操作日志</h2>
                <ul>
                    {logs.map((log) => (
                        <li key={log.id}>
                            [{new Date(log.timestamp).toLocaleString()}] {log.action}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default App;
