import React, { useEffect, useState } from 'react';
import { getLatestStatus, getStatusHistory, getUserLogs, sendControl } from './services/api';
import { createWebSocketConnection, } from './services/webSocketService'; // 引入 WebSocket 服务
import { Table, Button, Space, Card, Typography, message } from 'antd';
import './App.css';

const { Title } = Typography;

function App() {
    const [status, setStatus] = useState({
        temperature: 0,
        pressure: 0,
        depth: 0
    });
    const [history, setHistory] = useState([]);
    const [logs, setLogs] = useState([]);

    useEffect(() => {
        fetchLatestStatus();
        fetchHistory();
        fetchLogs();

        // 创建 WebSocket 连接
        const socket = createWebSocketConnection((message) => {
            const data = JSON.parse(message);
            const regex = /Temperature: ([\d.]+) °C, Pressure: ([\d.]+) kPa, Depth: ([\d.]+) m/;
            const match = data.message.match(regex);
            if (match) {
                const temperature = parseFloat(match[1]);
                const pressure = parseFloat(match[2]);
                const depth = parseFloat(match[3]);
                setStatus({ temperature, pressure, depth });
            }
        });

        // 清理 WebSocket 连接
        return () => {
            closeWebSocketConnection();
        };
    }, []);

    const fetchLatestStatus = async () => {
        try {
            const res = await getLatestStatus();
            setStatus(res.data || {
                temperature: 0,
                pressure: 0,
                depth: 0
            });
        } catch (err) {
            console.error(err);
        }
    };

    const fetchHistory = async () => {
        try {
            const res = await getStatusHistory();
            setHistory(res.data || []);
        } catch (err) {
            console.error(err);
        }
    };

    const fetchLogs = async () => {
        try {
            const res = await getUserLogs();
            setLogs(res.data || []);
        } catch (err) {
            console.error(err);
        }
    };

    const handleControl = async (direction) => {
        try {
            await sendControl(direction);
            message.success(`Control sent: ${direction}`);
        } catch (err) {
            console.error(err);
            message.error('Failed to send control');
        }
    };

    const historyColumns = [
        {
            title: '时间',
            dataIndex: 'timestamp',
            key: 'timestamp',
            render: text => new Date(text).toLocaleString(),
        },
        {
            title: '温度 (°C)',
            dataIndex: 'temperature',
            key: 'temperature',
        },
        {
            title: '气压 (kPa)',
            dataIndex: 'pressure',
            key: 'pressure',
        },
        {
            title: '深度 (m)',
            dataIndex: 'depth',
            key: 'depth',
        },
    ];

    const logsColumns = [
        {
            title: '时间',
            dataIndex: 'timestamp',
            key: 'timestamp',
            render: text => new Date(text).toLocaleString(),
        },
        {
            title: '用户操作',
            dataIndex: 'action',
            key: 'action',
        },
    ];

    return (
        <div className="App">
            <Title level={2} className="title">水下机器人控制平台</Title>
            <Space className="controls" direction="vertical">
                <Button type="primary" onClick={() => handleControl('forward')}>前进</Button>
                <Button onClick={() => handleControl('backward')}>后退</Button>
                <Button onClick={() => handleControl('left')}>左转</Button>
                <Button onClick={() => handleControl('right')}>右转</Button>
            </Space>
            <Card className="status-card">
                <Title level={3}>设备状态</Title>
                <div className="status">
                    <p>温度: {status.temperature} °C</p>
                    <p>气压: {status.pressure} kPa</p>
                    <p>深度: {status.depth} m</p>
                </div>
            </Card>
            <Card className="history-card">
                <Title level={3}>历史记录</Title>
                <Table
                    columns={historyColumns}
                    dataSource={history}
                    pagination={false}
                    scroll={{ y: '300px' }}
                />
            </Card>
            <Card className="logs-card">
                <Title level={3}>用户操作日志</Title>
                <Table
                    columns={logsColumns}
                    dataSource={logs}
                    pagination={false}
                    scroll={{ y: '300px' }}
                />
            </Card>
        </div>
    );
}

export default App;
